contracts/Dex.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Dex is Ownable {
    IERC20 public token;
    uint256 public totalWBTInContract;
    uint256 public totalWpoinsInContract;
    uint256 public buyFee = 2; // 2% fee for buying
    uint256 public sellFee = 2; // 2% fee for selling

    mapping(address => uint256) public liquidityProviderShares;

    event TokensPurchased(address indexed user, uint256 amount, uint256 cost);
    event TokensSold(address indexed user, uint256 amount, uint256 revenue);
    event LiquidityAdded(address indexed user, uint256 wbtAmount, uint256 wpoinsAmount);
    event LiquidityRemoved(address indexed user, uint256 wbtAmount, uint256 wpoinsAmount);

    constructor(address _token) Ownable(msg.sender) {
        token = IERC20(_token);
        totalWpoinsInContract = 130_000_000_000 * 10**18;
    }

    // User buys tokens with native token (WBT)
    function buyTokens() external payable {
        require(msg.value > 0, "Amount must be greater than 0");

        uint256 fee = (msg.value * buyFee) / 100;
        uint256 amountAfterFee = msg.value - fee;

        uint256 tokenAmount = (amountAfterFee * totalWpoinsInContract) / (totalWBTInContract + amountAfterFee);
        totalWBTInContract += amountAfterFee;
        totalWpoinsInContract -= tokenAmount;

        token.transfer(msg.sender, tokenAmount);

        emit TokensPurchased(msg.sender, tokenAmount, msg.value);
    }

    // User sells tokens and receives native token (WBT)
    function sellTokens(uint256 tokenAmount) external {
        require(tokenAmount > 0, "Amount must be greater than 0");
        require(token.transferFrom(msg.sender, address(this), tokenAmount), "Transfer failed");

        uint256 wbtAmount = (tokenAmount * totalWBTInContract) / (totalWpoinsInContract + tokenAmount);
        uint256 fee = (wbtAmount * sellFee) / 100;
        uint256 amountAfterFee = wbtAmount - fee;

        totalWBTInContract -= wbtAmount;
        totalWpoinsInContract += tokenAmount;

        payable(msg.sender).transfer(amountAfterFee);

        emit TokensSold(msg.sender, tokenAmount, amountAfterFee);
    }

    // Add liquidity to the pool
    function addLiquidity(uint256 wbtAmount, uint256 wpoinsAmount) external {
        require(wbtAmount > 0 && wpoinsAmount > 0, "Amounts must be greater than 0");

        // Transfer tokens from user to contract
        require(token.transferFrom(msg.sender, address(this), wpoinsAmount), "Transfer failed");
        payable(address(this)).transfer(wbtAmount);

        // Calculate liquidity provider shares
        uint256 totalLiquidity = totalWBTInContract + totalWpoinsInContract;
        uint256 shares = (wbtAmount + wpoinsAmount) * totalLiquidity / (totalWBTInContract + totalWpoinsInContract);

        // Update liquidity provider shares
        liquidityProviderShares[msg.sender] += shares;

        // Update pool balances
        totalWBTInContract += wbtAmount;
        totalWpoinsInContract += wpoinsAmount;

        emit LiquidityAdded(msg.sender, wbtAmount, wpoinsAmount);
    }

    // Remove liquidity from the pool
    function removeLiquidity(uint256 shares) external {
        require(shares > 0, "Shares must be greater than 0");
        require(liquidityProviderShares[msg.sender] >= shares, "Insufficient shares");

        // Calculate amounts to withdraw
        uint256 totalLiquidity = totalWBTInContract + totalWpoinsInContract;
        uint256 wbtAmount = (shares * totalWBTInContract) / totalLiquidity;
        uint256 wpoinsAmount = (shares * totalWpoinsInContract) / totalLiquidity;

        // Update liquidity provider shares
        liquidityProviderShares[msg.sender] -= shares;

        // Update pool balances
        totalWBTInContract -= wbtAmount;
        totalWpoinsInContract -= wpoinsAmount;

        // Transfer tokens to user
        payable(msg.sender).transfer(wbtAmount);
        token.transfer(msg.sender, wpoinsAmount);

        emit LiquidityRemoved(msg.sender, wbtAmount, wpoinsAmount);
    }

    // Adjust buy and sell fees (admin only)
    function adjustFees(uint256 newBuyFee, uint256 newSellFee) external onlyOwner {
        buyFee = newBuyFee;
        sellFee = newSellFee;
    }
}

contracts/Game.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Wpoins.sol";
import "./Marketing.sol";

contract Game is Ownable {
    Wpoins public token;
    Marketing public marketing;

    enum Choice { Rock, Scissors, Paper }
    enum Result { Win, Lose, Draw }

    struct GameDetail {
        uint256 gameId;
        address user;
        Choice userChoice;
        Choice contractChoice;
        Result result;
        uint256 betAmount;
        uint256 timestamp;
    }

    mapping(address => uint256[]) public userGameIds;
    mapping(uint256 => GameDetail) public gameDetails;
    uint256 public gameCounter;

    mapping(address => uint256) public totalWins;
    mapping(address => uint256) public totalLoses;
    mapping(address => uint256) public totalDraws;
    mapping(address => uint256) public totalWpoinsWon;
    mapping(address => uint256) public totalWpoinsLost;
    mapping(address => uint256) public totalBetAmount;

    event BetPlaced(address indexed user, uint256 gameId, Choice userChoice, Choice contractChoice, Result result, uint256 betAmount);

    constructor(address _token, address _marketing) Ownable(msg.sender) {
        token = Wpoins(_token);
        marketing = Marketing(_marketing);
    }

    // User places a bet
    function placeBet(Choice userChoice, uint256 betAmount) external {
        require(betAmount > 0, "Bet amount must be greater than 0");
        require(token.transferFrom(msg.sender, address(this), betAmount), "Bet failed");

        // Generate a random choice for the contract
        Choice contractChoice = Choice(uint256(keccak256(abi.encodePacked(block.timestamp, block.prevrandao, msg.sender))) % 3);

        // Determine the result
        Result result = determineResult(userChoice, contractChoice);

        // Update user statistics
        if (result == Result.Win) {
            uint256 winAmount = betAmount * 2;
            totalWins[msg.sender] += 1;
            totalWpoinsWon[msg.sender] += winAmount;
            token.transfer(msg.sender, winAmount);

            // Reward referrer if any
            marketing.rewardReferrer(msg.sender, winAmount);
        } else if (result == Result.Lose) {
            totalLoses[msg.sender] += 1;
            totalWpoinsLost[msg.sender] += betAmount;
        } else {
            totalDraws[msg.sender] += 1;
            // Return 80% of the bet amount to the user
            uint256 returnAmount = betAmount * 80 / 100;
            token.transfer(msg.sender, returnAmount);
        }

        // Update total bet amount
        totalBetAmount[msg.sender] += betAmount;

        // Store game details
        gameCounter++;
        GameDetail memory newGame = GameDetail({
            gameId: gameCounter,
            user: msg.sender,
            userChoice: userChoice,
            contractChoice: contractChoice,
            result: result,
            betAmount: betAmount,
            timestamp: block.timestamp
        });

        gameDetails[gameCounter] = newGame;
        userGameIds[msg.sender].push(gameCounter);

        emit BetPlaced(msg.sender, gameCounter, userChoice, contractChoice, result, betAmount);
    }

    // Determine the game result
    function determineResult(Choice userChoice, Choice contractChoice) internal pure returns (Result) {
        if (userChoice == contractChoice) {
            return Result.Draw;
        } else if (
            (userChoice == Choice.Rock && contractChoice == Choice.Scissors) ||
            (userChoice == Choice.Paper && contractChoice == Choice.Rock) ||
            (userChoice == Choice.Scissors && contractChoice == Choice.Paper)
        ) {
            return Result.Win;
        } else {
            return Result.Lose;
        }
    }

    // Get game details by ID
    function getGameDetailById(uint256 gameId) external view returns (GameDetail memory) {
        return gameDetails[gameId];
    }

    // Get game details by user
    function getGameDetailByUser(address user) external view returns (GameDetail[] memory) {
        uint256[] memory ids = userGameIds[user];
        GameDetail[] memory details = new GameDetail[](ids.length);

        for (uint256 i = 0; i < ids.length; i++) {
            details[i] = gameDetails[ids[i]];
        }

        return details;
    }

    // Get total games played by a user
    function getTotalGames(address user) public view returns (uint256) {
        return totalWins[user] + totalLoses[user] + totalDraws[user];
    }

    // Calculate winrate for a user
    function calculateWinrate(address user) public view returns (uint256) {
        uint256 totalGames = totalWins[user] + totalLoses[user] + totalDraws[user];
        if (totalGames == 0) return 0;
        return (totalWins[user] * 100) / totalGames;
    }

    // Calculate average bet amount for a user
    function calculateAverageBetAmount(address user) public view returns (uint256) {
        uint256 totalGames = totalWins[user] + totalLoses[user] + totalDraws[user];
        if (totalGames == 0) return 0;
        return totalBetAmount[user] / totalGames;
    }

    // Get total bet amount for a user
    function getTotalBetAmount(address user) public view returns (uint256) {
        return totalBetAmount[user];
    }

    // Get total Wpoins won by a user
    function getTotalWpoinsWon(address user) public view returns (uint256) {
        return totalWpoinsWon[user];
    }

    // Get total Wpoins lost by a user
    function getTotalWpoinsLost(address user) public view returns (uint256) {
        return totalWpoinsLost[user];
    }
}

contracts/Leaderboard.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Game.sol";

contract Leaderboard is Ownable {
    Game public game;

    struct LeaderboardEntry {
        address user;
        uint256 totalWins;
        uint256 totalLoses;
        uint256 totalDraws;
        uint256 totalWpoinsWon;
        uint256 totalWpoinsLost;
        uint256 totalBetAmount;
        uint256 winrate;
        uint256 averageBetAmount;
    }

    LeaderboardEntry[] public leaderboard;

    event LeaderboardUpdated(address indexed user, LeaderboardEntry entry);

    constructor(address _game) Ownable(msg.sender) {
        game = Game(_game);
    }

    // Update leaderboard for a user
    function updateLeaderboard(address user) external {
        uint256 totalWins = game.totalWins(user);
        uint256 totalLoses = game.totalLoses(user);
        uint256 totalDraws = game.totalDraws(user);
        uint256 totalWpoinsWon = game.getTotalWpoinsWon(user);
        uint256 totalWpoinsLost = game.getTotalWpoinsLost(user);
        uint256 totalBetAmount = game.getTotalBetAmount(user);
        uint256 winrate = game.calculateWinrate(user);
        uint256 averageBetAmount = game.calculateAverageBetAmount(user);

        LeaderboardEntry memory entry = LeaderboardEntry({
            user: user,
            totalWins: totalWins,
            totalLoses: totalLoses,
            totalDraws: totalDraws,
            totalWpoinsWon: totalWpoinsWon,
            totalWpoinsLost: totalWpoinsLost,
            totalBetAmount: totalBetAmount,
            winrate: winrate,
            averageBetAmount: averageBetAmount
        });

        // Check if user already exists in the leaderboard
        for (uint256 i = 0; i < leaderboard.length; i++) {
            if (leaderboard[i].user == user) {
                leaderboard[i] = entry;
                emit LeaderboardUpdated(user, entry);
                return;
            }
        }

        // If user does not exist, add a new entry
        leaderboard.push(entry);
        emit LeaderboardUpdated(user, entry);
    }

    // Get the top N players in the leaderboard
    function getTopPlayers(uint256 n) external view returns (LeaderboardEntry[] memory) {
        require(n <= leaderboard.length, "Requested number exceeds leaderboard size");

        LeaderboardEntry[] memory topPlayers = new LeaderboardEntry[](n);
        for (uint256 i = 0; i < n; i++) {
            topPlayers[i] = leaderboard[i];
        }
        return topPlayers;
    }

    // Get the leaderboard size
    function getLeaderboardSize() external view returns (uint256) {
        return leaderboard.length;
    }
}

contracts/Marketing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./Wpoins.sol";

contract Marketing is Ownable {
    Wpoins public token;

    struct UserInfo {
        string username;
        string avatar;
        string referralCode;
        uint256 referralCount;
        uint256 totalReferralEarnings;
        uint256 totalReferralWins;
    }

    mapping(address => UserInfo) public users;
    mapping(string => address) public codeToUser;

    uint256 public constant REFERRAL_REGISTRATION_REWARD = 1000 * 10**18; // 1000 Wpoins for referee
    uint256 public constant REFERRER_REWARD = 1000 * 10**18; // 1000 Wpoins for referrer
    uint256 public constant REFERRER_WIN_SHARE = 5; // 0.5% of referee's winnings

    event ReferralRegistered(address indexed user, string username, string avatar, string referralCode);
    event ReferralUsed(address indexed user, string referralCode, address indexed referrer);
    event ReferrerReward(address indexed referrer, address indexed referee, uint256 reward);

    constructor(address _token) Ownable(msg.sender) {
        token = Wpoins(_token);
    }

    // User registers with username, avatar, and optional referral code
    function register(string memory username, string memory avatar, string memory referralCode) external {
        require(bytes(users[msg.sender].username).length == 0, "User already registered");

        // Generate a unique referral code for the user
        string memory newReferralCode = generateReferralCode(msg.sender);

        // Save user info
        users[msg.sender] = UserInfo({
            username: username,
            avatar: avatar,
            referralCode: newReferralCode,
            referralCount: 0,
            totalReferralEarnings: 0,
            totalReferralWins: 0
        });

        codeToUser[newReferralCode] = msg.sender;
        emit ReferralRegistered(msg.sender, username, avatar, newReferralCode);

        // Use the referral code if provided
        if (bytes(referralCode).length > 0) {
            address referrer = codeToUser[referralCode];
            if (referrer != address(0) && referrer != msg.sender) {
                // Reward referrer and referee
                token.transfer(referrer, REFERRER_REWARD);
                token.transfer(msg.sender, REFERRAL_REGISTRATION_REWARD);

                // Increment referrer's referral count
                users[referrer].referralCount += 1;

                emit ReferralUsed(msg.sender, referralCode, referrer);
            }
        }
    }

    // Reward referrer when referee wins a game
    function rewardReferrer(address referee, uint256 winAmount) external onlyOwner {
        address referrer = codeToUser[users[referee].referralCode];
        if (referrer != address(0)) {
            uint256 referrerReward = (winAmount * REFERRER_WIN_SHARE) / 1000; // 0.5%
            token.transfer(referrer, referrerReward);
            users[referrer].totalReferralEarnings += referrerReward;
            users[referrer].totalReferralWins += 1;
            emit ReferrerReward(referrer, referee, referrerReward);
        }
    }

    // Generate a unique referral code
    function generateReferralCode(address user) private pure returns (string memory) {
        bytes32 hash = keccak256(abi.encodePacked(user));
        bytes memory code = new bytes(5);
        for (uint256 i = 0; i < 5; i++) {
            code[i] = _toHexChar(uint8(hash[i]) % 16);
        }
        return string(abi.encodePacked("0x", code));
    }

    function _toHexChar(uint8 value) private pure returns (bytes1) {
        if (value < 10) {
            return bytes1(value + 48); // 0-9
        } else {
            return bytes1(value + 87); // a-f
        }
    }
}

